mapper.select:
SELECT * FROM POINT WHERE MB_ID = #{mb_id}
AND 0 > PI_NO
AND PI_NO > 0
ORDER BY PI_NUM DESC LIMIT #{cri.pageStart}, #{cri.perPageNum}

SELECT * FROM INQUIRY WHERE IQ_TITLE LIKE CONCAT('%', #{cri.search}, '%')
AND MB_ID = #{mb_id}
ORDER BY IQ_NUM DESC LIMIT #{cri.pageStart}, #{cri.perPageNum}
